mapper.select:

where sale_user_id = #{userId}
and status not in ('2','9')
limit 1

mapper.update:
update sys_sale_coin
remain_amount = #{remainAmount}, split_min_rate = #{splitMinRate}, split_max_rate = #{splitMaxRate}, single_buy_max_amount = #{singleBuyMaxAmount}, status = #{status}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where sale_id = #{saleId}

mapper.select:
SELECT MAIN.sale_id, MAIN.sale_no, MAIN.sale_time, MAIN.sale_user_id, MAIN.sale_split_type, MAIN.support_buy_type, MAIN.sale_amount, MAIN.sale_amount_without_commission, MAIN.commission_amount, MAIN.remain_amount, MAIN.split_min_rate, MAIN.split_max_rate, TRUNCATE(MAIN.split_min_rate * MAIN.remain_amount/100,2) AS split_min_amount, TRUNCATE(MAIN.split_max_rate * MAIN.remain_amount/100,2) AS split_max_amount, MAIN.single_buy_max_amount, MAIN.status, MAIN.urgent_sale_flg, MAIN.nick_name, MAIN.phonenumber, MAIN.real_name, MAIN.idcard_no, MAIN.avatar, MAIN.wechat_pay_remark, MAIN.wechat_pay_img, MAIN.alipay_remark, MAIN.alipay_img, MAIN.unionpay_remark, MAIN.unionpay_account, MAIN.unionpay_card, MAIN.alipay_approve_flg, MAIN.wechat_pay_approve_flg FROM(
SELECT s.sale_id, s.sale_no, s.sale_time, s.sale_user_id, s.sale_split_type, s.support_buy_type, s.sale_amount, s.sale_amount_without_commission, s.commission_amount, s.remain_amount, s.split_min_rate, s.split_max_rate, s.single_buy_max_amount, s.status, s.urgent_sale_flg, u.nick_name, u.phonenumber, u.real_name, u.idcard_no, u.avatar, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_remark else null end as wechat_pay_remark, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_img else null end as wechat_pay_img, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_remark else null end as alipay_remark, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_img else null end as alipay_img, u.unionpay_remark, u.unionpay_account, u.unionpay_card, case when IFNULL(alipay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as alipay_approve_flg, case when IFNULL(wechatpay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as wechat_pay_approve_flg FROM sys_sale_coin s INNER JOIN sys_user u ON s.sale_user_id = u.user_id LEFT JOIN sys_dept d ON u.dept_id = d.dept_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='0' group by a.user_id) alipayNoApprove on alipayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='1' group by a.user_id) alipay on alipay.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='0' group by a.user_id) wechatpayNoApprove on wechatpayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='1' group by a.user_id) wechatpay on wechatpay.user_id=u.user_id WHERE s.status = '1' AND ( u.user_id = #{merchantUserId} OR d.dept_id IN ( SELECT dept_id FROM sys_dept WHERE find_in_set( #{deptId} , ancestors ) ) ) AND u.user_id != #{userId}
AND s.sale_amount >= #{saleAmountFrom}
AND s.sale_amount <= #{saleAmountTo}
AND s.sale_split_type = #{saleSplitType}
AND
(s.support_buy_type LIKE CONCAT(CONCAT('%',#{dictCode}),'%'))
)MAIN order by MAIN.sale_id DESC
limit #{pageNumber} , #{pageRowCount}

SELECT s.sale_id, s.sale_no, s.sale_time, s.sale_user_id, s.sale_split_type, s.support_buy_type, s.sale_amount, s.sale_amount_without_commission, s.commission_amount, s.remain_amount, s.split_min_rate, s.split_max_rate, TRUNCATE(s.split_min_rate * s.remain_amount/100,2) AS split_min_amount, TRUNCATE(s.split_max_rate * s.remain_amount/100,2) AS split_max_amount, s.single_buy_max_amount, s.status, s.urgent_sale_flg, u.nick_name, u.phonenumber, u.real_name, u.idcard_no, u.avatar, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_remark else null end as wechat_pay_remark, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_img else null end as wechat_pay_img, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_remark else null end as alipay_remark, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_img else null end as alipay_img, u.unionpay_remark, u.unionpay_account, u.unionpay_card, case when IFNULL(alipay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as alipay_approve_flg, case when IFNULL(wechatpay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as wechat_pay_approve_flg FROM sys_user u INNER JOIN sys_sale_coin s ON s.sale_user_id = u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='0' group by a.user_id) alipayNoApprove on alipayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='1' group by a.user_id) alipay on alipay.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='0' group by a.user_id) wechatpayNoApprove on wechatpayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='1' group by a.user_id) wechatpay on wechatpay.user_id=u.user_id WHERE u.del_flag = '0' and s.sale_user_id = #{userId}
and s.status = #{status}
AND s.sale_amount >= #{saleAmountFrom}
AND s.sale_amount <= #{saleAmountTo}
AND s.sale_split_type = #{saleSplitType}
AND
(s.support_buy_type LIKE CONCAT(CONCAT('%',#{dictCode}),'%'))
order by s.sale_id DESC
limit #{pageNumber} , #{pageRowCount}

SELECT MAIN.sale_id, MAIN.sale_no, MAIN.sale_time, MAIN.sale_user_id, MAIN.sale_split_type, MAIN.support_buy_type, MAIN.sale_amount, MAIN.sale_amount_without_commission, MAIN.commission_amount, MAIN.remain_amount, MAIN.split_min_rate, MAIN.split_max_rate, TRUNCATE(MAIN.split_min_rate * MAIN.remain_amount/100,2) AS split_min_amount, TRUNCATE(MAIN.split_max_rate * MAIN.remain_amount/100,2) AS split_max_amount, MAIN.single_buy_max_amount, MAIN.status, MAIN.urgent_sale_flg, MAIN.nick_name, MAIN.phonenumber, MAIN.real_name, MAIN.idcard_no, MAIN.avatar, MAIN.wechat_pay_remark, MAIN.wechat_pay_img, MAIN.alipay_remark, MAIN.alipay_img, MAIN.unionpay_remark, MAIN.unionpay_account, MAIN.unionpay_card, MAIN.alipay_approve_flg, MAIN.wechat_pay_approve_flg FROM( SELECT s.sale_id, s.sale_no, s.sale_time, s.sale_user_id, s.sale_split_type, s.support_buy_type, s.sale_amount, s.sale_amount_without_commission, s.commission_amount, s.remain_amount, s.split_min_rate, s.split_max_rate, s.single_buy_max_amount, s.status, s.urgent_sale_flg, u.nick_name, u.phonenumber, u.real_name, u.idcard_no, u.avatar, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_remark else null end as wechat_pay_remark, case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_img else null end as wechat_pay_img, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_remark else null end as alipay_remark, case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_img else null end as alipay_img, u.unionpay_remark, u.unionpay_account, u.unionpay_card, case when IFNULL(alipay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as alipay_approve_flg, case when IFNULL(wechatpay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as wechat_pay_approve_flg FROM sys_sale_coin s INNER JOIN sys_user u ON s.sale_user_id = u.user_id LEFT JOIN sys_dept d ON u.dept_id = d.dept_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='0' group by a.user_id) alipayNoApprove on alipayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='1' group by a.user_id) alipay on alipay.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='0' group by a.user_id) wechatpayNoApprove on wechatpayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='1' group by a.user_id) wechatpay on wechatpay.user_id=u.user_id WHERE ( #{userId} = 1 or s.sale_user_id = #{userId} or d.dept_id IN ( SELECT dept_id FROM sys_dept WHERE find_in_set( #{deptId} , ancestors ) ) )
AND s.sale_user_id = #{saleUserId}
AND s.status = #{status}
AND s.sale_no like concat('%', #{saleNo}, '%')
AND s.sale_split_type = #{saleSplitType}
AND
(s.support_buy_type LIKE CONCAT(CONCAT('%',#{dictCode}),'%'))
AND s.urgent_sale_flg = #{urgentSaleFlg}
and date_format(s.sale_time,'%y%m%d') >= date_format(#{startSaleTime},'%y%m%d')
and date_format(s.sale_time,'%y%m%d') <= date_format(#{endSaleTime},'%y%m%d')
)MAIN order by MAIN.sale_id asc
