mapper.select:


     and tr.user_id = #{userId}
     and tr.record_time = #{recordTime}
     and tr.record_type = #{recordType}
     and tr.buy_id = #{buyId}
     and tr.sale_id = #{saleId}
     and tr.recharge_id = #{rechargeId}
     and tr.record_amount = #{recordAmount}
     and tr.user_remain_amount = #{userRemainAmount}
     and tr.status = #{status}

        and date_format(tr.record_time,'%y%m%d') >= STR_TO_DATE(#{startTime}, '%Y-%m-%d')


        and date_format(tr.record_time,'%y%m%d') <= STR_TO_DATE(#{endTime}, '%Y-%m-%d')


        and
        (
        u.user_id = 1
        or
        de.dept_id IN ( SELECT dept_id FROM sys_dept WHERE find_in_set( #{deptId} , ancestors ) )
        )


order by record_time desc

mapper.update:
update sys_transaction_record
status = #{status}, record_amount = #{recordAmount}, user_remain_amount = #{userRemainAmount}, update_by = #{updateBy}, update_time = sysdate()
where record_id = #{recordId}

mapper.select:

where tr.record_type = #{recordType}

	and tr.buy_id = #{buyId}


	and tr.sale_id = #{saleId}


	and tr.recharge_id = #{rechargeId}

limit 1
