mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex,u.pay_password, u.real_name, u.idcard_no, u.idcard_front_img, u.idcard_back_img, u.face_img, u.wallet_address, u.amount, u.invite_code , case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_remark else null end as wechat_pay_remark , case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_img else null end as wechat_pay_remark , case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_remark else null end as alipay_remark , case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_img else null end as alipay_img , u.unionpay_remark, u.unionpay_account, u.unionpay_card, u.user_type, u.parent_user_id, u.ungent_commission, u.normal_commission, u.create_agent_flg, u.split_min_rate, u.split_max_rate, u.single_buy_max_amount, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader ,case when IFNULL(alipay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as alipay_approve_flg ,case when IFNULL(wechatpay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as wechat_pay_approve_flg from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='0' group by a.user_id) alipayNoApprove on alipayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='1' group by a.user_id) alipay on alipay.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='0' group by a.user_id) wechatpayNoApprove on wechatpayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='1' group by a.user_id) wechatpay on wechatpay.user_id=u.user_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber,u.pay_password, u.real_name, u.idcard_no, u.idcard_front_img, u.idcard_back_img, u.face_img, u.wallet_address, u.amount, u.invite_code , case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_remark else null end as wechat_pay_remark , case when IFNULL(wechatpay.approveCnt,0) > 0 then u.wechat_pay_img else null end as wechat_pay_remark , case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_remark else null end as alipay_remark , case when IFNULL(alipay.approveCnt,0) > 0 then u.alipay_img else null end as alipay_img , u.unionpay_remark, u.unionpay_account, u.unionpay_card, u.user_type, u.parent_user_id, u.ungent_commission, u.normal_commission, u.create_agent_flg, u.split_min_rate, u.split_max_rate, u.single_buy_max_amount, u.status, u.create_time ,case when IFNULL(alipay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as alipay_approve_flg ,case when IFNULL(wechatpay.approveCnt,0) > 0 then '1' when IFNULL(wechatpayNoApprove.approveCnt,0) > 0 then '0' else '2' end as wechat_pay_approve_flg from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='0' group by a.user_id) alipayNoApprove on alipayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='0' and a.status='1' group by a.user_id) alipay on alipay.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='0' group by a.user_id) wechatpayNoApprove on wechatpayNoApprove.user_id=u.user_id left join (select count(*) as approveCnt,a.user_id from sys_user_pay_type_approve a where a.pay_type='1' and a.status='1' group by a.user_id) wechatpay on wechatpay.user_id=u.user_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, pay_password = #{payPassword}, real_name = #{realName}, idcard_no = #{idcardNo}, idcard_front_img = #{idcardFrontImg}, idcard_back_img = #{idcardBackImg}, face_img = #{faceImg}, wallet_address = #{walletAddress}, amount = #{amount}, invite_code = #{inviteCode}, wechat_pay_remark = #{wechatPayRemark}, wechat_pay_img = #{wechatPayImg}, alipay_remark = #{alipayRemark}, alipay_img = #{alipayImg}, unionpay_remark = #{unionpayRemark}, unionpay_account = #{unionpayAccount}, unionpay_card = #{unionpayCard}, user_type = #{userType}, parent_user_id = #{parentUserId}, ungent_commission = #{ungentCommission}, normal_commission = #{normalCommission}, create_agent_flg = #{createAgentFlg}, split_min_rate = #{splitMinRate}, split_max_rate = #{splitMaxRate}, single_buy_max_amount = #{singleBuyMaxAmount}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
